                    foreach($this->collMovimientoBancos as $obj) {
                        if($obj->isNew()) {
                            $collMovimientoBancos[] = $obj;
                        }
                    }
                }

                $this->collMovimientoBancos = $collMovimientoBancos;
                $this->collMovimientoBancosPartial = false;
