    /**
     * Sets a collection of MovimientoBanco objects related by a one-to-many relationship
     * to the current object.
     * It will also schedule objects for deletion based on a diff between old objects (aka persisted)
     * and new objects from the given Propel collection.
     *
     * @param PropelCollection $movimientoBancos A Propel collection.
     * @param PropelPDO $con Optional connection object
     */
    public function setMovimientoBancos(PropelCollection $movimientoBancos, PropelPDO $con = null)
    {
        $this->movimientoBancosScheduledForDeletion = $this->getMovimientoBancos(new Criteria(), $con)->diff($movimientoBancos);

        foreach ($this->movimientoBancosScheduledForDeletion as $movimientoBancoRemoved) {
            $movimientoBancoRemoved->setBitacoraArchivo(null);
        }

        $this->collMovimientoBancos = null;
        foreach ($movimientoBancos as $movimientoBanco) {
            $this->addMovimientoBanco($movimientoBanco);
        }

        $this->collMovimientoBancos = $movimientoBancos;
        $this->collMovimientoBancosPartial = false;
    }

    /**
     * Returns the number of related MovimientoBanco objects.
     *
     * @param Criteria $criteria
     * @param boolean $distinct
     * @param PropelPDO $con
     * @return int             Count of related MovimientoBanco objects.
     * @throws PropelException
     */
    public function countMovimientoBancos(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
    {
        $partial = $this->collMovimientoBancosPartial && !$this->isNew();
        if (null === $this->collMovimientoBancos || null !== $criteria || $partial) {
            if ($this->isNew() && null === $this->collMovimientoBancos) {
                return 0;
            } else {
                if($partial && !$criteria) {
                    return count($this->getMovimientoBancos());
                }
                $query = MovimientoBancoQuery::create(null, $criteria);
                if ($distinct) {
                    $query->distinct();
                }

                return $query
                    ->filterByBitacoraArchivo($this)
                    ->count($con);
            }
        } else {
            return count($this->collMovimientoBancos);
        }
    }

    /**
     * Method called to associate a MovimientoBanco object to this object
     * through the MovimientoBanco foreign key attribute.
     *
     * @param    MovimientoBanco $l MovimientoBanco
     * @return BitacoraArchivo The current object (for fluent API support)
     */
    public function addMovimientoBanco(MovimientoBanco $l)
    {
        if ($this->collMovimientoBancos === null) {
            $this->initMovimientoBancos();
            $this->collMovimientoBancosPartial = true;
        }
        if (!$this->collMovimientoBancos->contains($l)) { // only add it if the **same** object is not already associated
            $this->doAddMovimientoBanco($l);
        }

        return $this;
    }

    /**
     * @param	MovimientoBanco $movimientoBanco The movimientoBanco object to add.
     */
    protected function doAddMovimientoBanco($movimientoBanco)
    {
        $this->collMovimientoBancos[]= $movimientoBanco;
        $movimientoBanco->setBitacoraArchivo($this);
    }

    /**
     * @param	MovimientoBanco $movimientoBanco The movimientoBanco object to remove.
     */
    public function removeMovimientoBanco($movimientoBanco)
    {
        if ($this->getMovimientoBancos()->contains($movimientoBanco)) {
            $this->collMovimientoBancos->remove($this->collMovimientoBancos->search($movimientoBanco));
            if (null === $this->movimientoBancosScheduledForDeletion) {
                $this->movimientoBancosScheduledForDeletion = clone $this->collMovimientoBancos;
                $this->movimientoBancosScheduledForDeletion->clear();
            }
            $this->movimientoBancosScheduledForDeletion[]= $movimientoBanco;
            $movimientoBanco->setBitacoraArchivo(null);
        }
    }


