     */
    public function clearAllReferences($deep = false)
    {
        if ($deep) {
            if ($this->collMovimientoBancos) {
                foreach ($this->collMovimientoBancos as $o) {
                    $o->clearAllReferences($deep);
                }
            }
        } // if ($deep)

        if ($this->collMovimientoBancos instanceof PropelCollection) {
            $this->collMovimientoBancos->clearIterator();
        }
        $this->collMovimientoBancos = null;
        $this->aEmpresa = null;
    }

