    public function getValorUsuarios($criteria = null, PropelPDO $con = null)
    {
        $partial = $this->collValorUsuariosPartial && !$this->isNew();
        if (null === $this->collValorUsuarios || null !== $criteria  || $partial) {
            if ($this->isNew() && null === $this->collValorUsuarios) {
