                $this->initValorUsuarios();
            } else {
                $collValorUsuarios = ValorUsuarioQuery::create(null, $criteria)
                    ->filterByCampoUsuario($this)
                    ->find($con);
                if (null !== $criteria) {
                    if (false !== $this->collValorUsuariosPartial && count($collValorUsuarios)) {
