                        if (false == $this->collValorUsuarios->contains($obj)) {
                          $this->collValorUsuarios->append($obj);
                        }
                      }

                      $this->collValorUsuariosPartial = true;
                    }

                    return $collValorUsuarios;
                }

                if($partial && $this->collValorUsuarios) {
                    foreach($this->collValorUsuarios as $obj) {
                        if($obj->isNew()) {
