    public function countValorUsuarios(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
    {
        $partial = $this->collValorUsuariosPartial && !$this->isNew();
        if (null === $this->collValorUsuarios || null !== $criteria || $partial) {
            if ($this->isNew() && null === $this->collValorUsuarios) {
                return 0;
            } else {
                if($partial && !$criteria) {
                    return count($this->getValorUsuarios());
                }
                $query = ValorUsuarioQuery::create(null, $criteria);
                if ($distinct) {
                    $query->distinct();
                }

                return $query
                    ->filterByCampoUsuario($this)
                    ->count($con);
