    public function removeValorUsuario($valorUsuario)
    {
        if ($this->getValorUsuarios()->contains($valorUsuario)) {
            $this->collValorUsuarios->remove($this->collValorUsuarios->search($valorUsuario));
            if (null === $this->valorUsuariosScheduledForDeletion) {
                $this->valorUsuariosScheduledForDeletion = clone $this->collValorUsuarios;
                $this->valorUsuariosScheduledForDeletion->clear();
            }
            $this->valorUsuariosScheduledForDeletion[]= $valorUsuario;
            $valorUsuario->setCampoUsuario(null);
        }
    }


