    /**
     * Gets an array of CierreCheck objects which contain a foreign key that references this object.
     *
     * If the $criteria is not null, it is used to always fetch the results from the database.
     * Otherwise the results are fetched from the database the first time, then cached.
     * Next time the same method is called without $criteria, the cached collection is returned.
     * If this Cierre is new, it will return
     * an empty collection or the current collection; the criteria is ignored on a new object.
     *
     * @param Criteria $criteria optional Criteria object to narrow the query
     * @param PropelPDO $con optional connection object
     * @return PropelObjectCollection|CierreCheck[] List of CierreCheck objects
     * @throws PropelException
     */
    public function getCierreChecks($criteria = null, PropelPDO $con = null)
    {
        $partial = $this->collCierreChecksPartial && !$this->isNew();
        if (null === $this->collCierreChecks || null !== $criteria  || $partial) {
            if ($this->isNew() && null === $this->collCierreChecks) {
                // return empty collection
                $this->initCierreChecks();
            } else {
                $collCierreChecks = CierreCheckQuery::create(null, $criteria)
                    ->filterByCierre($this)
                    ->find($con);
                if (null !== $criteria) {
                    if (false !== $this->collCierreChecksPartial && count($collCierreChecks)) {
                      $this->initCierreChecks(false);

                      foreach($collCierreChecks as $obj) {
                        if (false == $this->collCierreChecks->contains($obj)) {
                          $this->collCierreChecks->append($obj);
                        }
                      }

                      $this->collCierreChecksPartial = true;
                    }

                    return $collCierreChecks;
                }

                if($partial && $this->collCierreChecks) {
                    foreach($this->collCierreChecks as $obj) {
                        if($obj->isNew()) {
                            $collCierreChecks[] = $obj;
                        }
                    }
                }

                $this->collCierreChecks = $collCierreChecks;
                $this->collCierreChecksPartial = false;
            }
        }

        return $this->collCierreChecks;
    }

    /**
     * Sets a collection of CierreCheck objects related by a one-to-many relationship
     * to the current object.
     * It will also schedule objects for deletion based on a diff between old objects (aka persisted)
     * and new objects from the given Propel collection.
     *
     * @param PropelCollection $cierreChecks A Propel collection.
     * @param PropelPDO $con Optional connection object
     */
    public function setCierreChecks(PropelCollection $cierreChecks, PropelPDO $con = null)
    {
        $this->cierreChecksScheduledForDeletion = $this->getCierreChecks(new Criteria(), $con)->diff($cierreChecks);

        foreach ($this->cierreChecksScheduledForDeletion as $cierreCheckRemoved) {
            $cierreCheckRemoved->setCierre(null);
        }

        $this->collCierreChecks = null;
        foreach ($cierreChecks as $cierreCheck) {
            $this->addCierreCheck($cierreCheck);
        }

        $this->collCierreChecks = $cierreChecks;
        $this->collCierreChecksPartial = false;
    }

    /**
     * Returns the number of related CierreCheck objects.
     *
     * @param Criteria $criteria
     * @param boolean $distinct
     * @param PropelPDO $con
     * @return int             Count of related CierreCheck objects.
     * @throws PropelException
     */
    public function countCierreChecks(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
    {
        $partial = $this->collCierreChecksPartial && !$this->isNew();
        if (null === $this->collCierreChecks || null !== $criteria || $partial) {
            if ($this->isNew() && null === $this->collCierreChecks) {
                return 0;
            } else {
                if($partial && !$criteria) {
                    return count($this->getCierreChecks());
                }
                $query = CierreCheckQuery::create(null, $criteria);
                if ($distinct) {
                    $query->distinct();
                }

                return $query
                    ->filterByCierre($this)
                    ->count($con);
            }
        } else {
            return count($this->collCierreChecks);
        }
    }

    /**
     * Method called to associate a CierreCheck object to this object
     * through the CierreCheck foreign key attribute.
