     * Sets a collection of CierreCheck objects related by a one-to-many relationship
     * to the current object.
     * It will also schedule objects for deletion based on a diff between old objects (aka persisted)
     * and new objects from the given Propel collection.
     *
     * @param PropelCollection $cierreChecks A Propel collection.
     * @param PropelPDO $con Optional connection object
     */
    public function setCierreChecks(PropelCollection $cierreChecks, PropelPDO $con = null)
    {
        $this->cierreChecksScheduledForDeletion = $this->getCierreChecks(new Criteria(), $con)->diff($cierreChecks);

        foreach ($this->cierreChecksScheduledForDeletion as $cierreCheckRemoved) {
            $cierreCheckRemoved->setCierreCaja(null);
        }

        $this->collCierreChecks = null;
        foreach ($cierreChecks as $cierreCheck) {
            $this->addCierreCheck($cierreCheck);
        }

        $this->collCierreChecks = $cierreChecks;
        $this->collCierreChecksPartial = false;
    }

    /**
     * Returns the number of related CierreCheck objects.
     *
     * @param Criteria $criteria
     * @param boolean $distinct
     * @param PropelPDO $con
     * @return int             Count of related CierreCheck objects.
     * @throws PropelException
     */
    public function countCierreChecks(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
    {
        $partial = $this->collCierreChecksPartial && !$this->isNew();
        if (null === $this->collCierreChecks || null !== $criteria || $partial) {
            if ($this->isNew() && null === $this->collCierreChecks) {
                return 0;
            } else {
                if($partial && !$criteria) {
                    return count($this->getCierreChecks());
                }
                $query = CierreCheckQuery::create(null, $criteria);
                if ($distinct) {
                    $query->distinct();
                }

                return $query
                    ->filterByCierreCaja($this)
                    ->count($con);
            }
        } else {
            return count($this->collCierreChecks);
        }
    }

    /**
     * Method called to associate a CierreCheck object to this object
     * through the CierreCheck foreign key attribute.
     *
     * @param    CierreCheck $l CierreCheck
     * @return CierreCaja The current object (for fluent API support)
     */
    public function addCierreCheck(CierreCheck $l)
    {
        if ($this->collCierreChecks === null) {
            $this->initCierreChecks();
            $this->collCierreChecksPartial = true;
        }
        if (!$this->collCierreChecks->contains($l)) { // only add it if the **same** object is not already associated
            $this->doAddCierreCheck($l);
        }

        return $this;
    }

    /**
     * @param	CierreCheck $cierreCheck The cierreCheck object to add.
     */
    protected function doAddCierreCheck($cierreCheck)
    {
        $this->collCierreChecks[]= $cierreCheck;
        $cierreCheck->setCierreCaja($this);
    }

    /**
     * @param	CierreCheck $cierreCheck The cierreCheck object to remove.
     */
    public function removeCierreCheck($cierreCheck)
    {
        if ($this->getCierreChecks()->contains($cierreCheck)) {
            $this->collCierreChecks->remove($this->collCierreChecks->search($cierreCheck));
            if (null === $this->cierreChecksScheduledForDeletion) {
                $this->cierreChecksScheduledForDeletion = clone $this->collCierreChecks;
                $this->cierreChecksScheduledForDeletion->clear();
            }
            $this->cierreChecksScheduledForDeletion[]= $cierreCheck;
            $cierreCheck->setCierreCaja(null);
        }
    }


