                // We no longer rehydrate the object, since this can cause data loss.
                // See http://www.propelorm.org/ticket/509
                // $obj1->hydrate($row, 0, true); // rehydrate
            } else {
                $cls = CuentaBancoPeer::getOMClass();

                $obj1 = new $cls();
                $obj1->hydrate($row);
                CuentaBancoPeer::addInstanceToPool($obj1, $key1);
            } // if obj1 already loaded

                // Add objects for joined Banco rows

                $key2 = BancoPeer::getPrimaryKeyHashFromRow($row, $startcol2);
                if ($key2 !== null) {
                    $obj2 = BancoPeer::getInstanceFromPool($key2);
                    if (!$obj2) {

                        $cls = BancoPeer::getOMClass();

                    $obj2 = new $cls();
                    $obj2->hydrate($row, $startcol2);
                    BancoPeer::addInstanceToPool($obj2, $key2);
                } // if $obj2 already loaded

                // Add the $obj1 (CuentaBanco) to the collection in $obj2 (Banco)
                $obj2->addCuentaBanco($obj1);

            } // if joined row is not null

                // Add objects for joined MovimientoBanco rows

                $key3 = MovimientoBancoPeer::getPrimaryKeyHashFromRow($row, $startcol3);
                if ($key3 !== null) {
                    $obj3 = MovimientoBancoPeer::getInstanceFromPool($key3);
                    if (!$obj3) {

                        $cls = MovimientoBancoPeer::getOMClass();

                    $obj3 = new $cls();
                    $obj3->hydrate($row, $startcol3);
                    MovimientoBancoPeer::addInstanceToPool($obj3, $key3);
                } // if $obj3 already loaded

                // Add the $obj1 (CuentaBanco) to the collection in $obj3 (MovimientoBanco)
                $obj3->addCuentaBanco($obj1);

            } // if joined row is not null

                // Add objects for joined OperacionPago rows

                $key4 = OperacionPagoPeer::getPrimaryKeyHashFromRow($row, $startcol4);
                if ($key4 !== null) {
                    $obj4 = OperacionPagoPeer::getInstanceFromPool($key4);
                    if (!$obj4) {

                        $cls = OperacionPagoPeer::getOMClass();

                    $obj4 = new $cls();
                    $obj4->hydrate($row, $startcol4);
                    OperacionPagoPeer::addInstanceToPool($obj4, $key4);
                } // if $obj4 already loaded

                // Add the $obj1 (CuentaBanco) to the collection in $obj4 (OperacionPago)
                $obj4->addCuentaBanco($obj1);

            } // if joined row is not null

            $results[] = $obj1;
        }
        $stmt->closeCursor();

        return $results;
    }

