    public static function retrieveByPK($pk, PropelPDO $con = null)
    {

        if (null !== ($obj = CuentaBancoPeer::getInstanceFromPool((string) $pk))) {
            return $obj;
        }

        if ($con === null) {
            $con = Propel::getConnection(CuentaBancoPeer::DATABASE_NAME, Propel::CONNECTION_READ);
        }

        $criteria = new Criteria(CuentaBancoPeer::DATABASE_NAME);
        $criteria->add(CuentaBancoPeer::ID, $pk);

        $v = CuentaBancoPeer::doSelect($criteria, $con);

        return !empty($v) > 0 ? $v[0] : null;
