     * <code>
     * $query->filterByCodigoCargo('fooValue');   // WHERE codigo_cargo = 'fooValue'
     * $query->filterByCodigoCargo('%fooValue%'); // WHERE codigo_cargo LIKE '%fooValue%'
     * </code>
     *
     * @param     string $codigoCargo The value to use as filter.
     *              Accepts wildcards (* and % trigger a LIKE)
     * @param     string $comparison Operator to use for the column comparison, defaults to Criteria::EQUAL
     *
     * @return DepartamentoQuery The current query, for fluid interface
     */
    public function filterByCodigoCargo($codigoCargo = null, $comparison = null)
    {
        if (null === $comparison) {
            if (is_array($codigoCargo)) {
                $comparison = Criteria::IN;
            } elseif (preg_match('/[\%\*]/', $codigoCargo)) {
                $codigoCargo = str_replace('*', '%', $codigoCargo);
                $comparison = Criteria::LIKE;
            }
        }

        return $this->addUsingAlias(DepartamentoPeer::CODIGO_CARGO, $codigoCargo, $comparison);
    }

