        $this->collCheques = null; // important to set this to null since that means it is uninitialized
        $this->collChequesPartial = null;
    }

    /**
     * reset is the collCheques collection loaded partially
     *
     * @return void
     */
    public function resetPartialCheques($v = true)
    {
        $this->collChequesPartial = $v;
    }

    /**
     * Initializes the collCheques collection.
     *
     * By default this just sets the collCheques collection to an empty array (like clearcollCheques());
     * however, you may wish to override this method in your stub class to provide setting appropriate
     * to your application -- for example, setting the initial array to the values stored in database.
     *
     * @param boolean $overrideExisting If set to true, the method call initializes
     *                                        the collection even if it is not empty
     *
     * @return void
     */
    public function initCheques($overrideExisting = true)
    {
        if (null !== $this->collCheques && !$overrideExisting) {
            return;
        }
        $this->collCheques = new PropelObjectCollection();
        $this->collCheques->setModel('Cheque');
    }

    /**
     * Gets an array of Cheque objects which contain a foreign key that references this object.
     *
     * If the $criteria is not null, it is used to always fetch the results from the database.
     * Otherwise the results are fetched from the database the first time, then cached.
     * Next time the same method is called without $criteria, the cached collection is returned.
     * If this DocumentoCheque is new, it will return
     * an empty collection or the current collection; the criteria is ignored on a new object.
     *
     * @param Criteria $criteria optional Criteria object to narrow the query
     * @param PropelPDO $con optional connection object
     * @return PropelObjectCollection|Cheque[] List of Cheque objects
     * @throws PropelException
     */
    public function getCheques($criteria = null, PropelPDO $con = null)
    {
        $partial = $this->collChequesPartial && !$this->isNew();
        if (null === $this->collCheques || null !== $criteria  || $partial) {
            if ($this->isNew() && null === $this->collCheques) {
                // return empty collection
                $this->initCheques();
            } else {
                $collCheques = ChequeQuery::create(null, $criteria)
                    ->filterByDocumentoCheque($this)
                    ->find($con);
                if (null !== $criteria) {
                    if (false !== $this->collChequesPartial && count($collCheques)) {
                      $this->initCheques(false);

                      foreach($collCheques as $obj) {
                        if (false == $this->collCheques->contains($obj)) {
                          $this->collCheques->append($obj);
                        }
                      }

                      $this->collChequesPartial = true;
                    }

                    return $collCheques;
                }

                if($partial && $this->collCheques) {
                    foreach($this->collCheques as $obj) {
                        if($obj->isNew()) {
                            $collCheques[] = $obj;
                        }
                    }
                }

                $this->collCheques = $collCheques;
                $this->collChequesPartial = false;
            }
        }

        return $this->collCheques;
    }

    /**
     * Sets a collection of Cheque objects related by a one-to-many relationship
     * to the current object.
     * It will also schedule objects for deletion based on a diff between old objects (aka persisted)
     * and new objects from the given Propel collection.
     *
     * @param PropelCollection $cheques A Propel collection.
     * @param PropelPDO $con Optional connection object
     */
    public function setCheques(PropelCollection $cheques, PropelPDO $con = null)
    {
        $this->chequesScheduledForDeletion = $this->getCheques(new Criteria(), $con)->diff($cheques);

        foreach ($this->chequesScheduledForDeletion as $chequeRemoved) {
            $chequeRemoved->setDocumentoCheque(null);
        }

        $this->collCheques = null;
        foreach ($cheques as $cheque) {
            $this->addCheque($cheque);
        }

        $this->collCheques = $cheques;
        $this->collChequesPartial = false;
    }

    /**
     * Returns the number of related Cheque objects.
     *
     * @param Criteria $criteria
     * @param boolean $distinct
     * @param PropelPDO $con
     * @return int             Count of related Cheque objects.
     * @throws PropelException
     */
    public function countCheques(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
    {
        $partial = $this->collChequesPartial && !$this->isNew();
        if (null === $this->collCheques || null !== $criteria || $partial) {
            if ($this->isNew() && null === $this->collCheques) {
                return 0;
            } else {
                if($partial && !$criteria) {
                    return count($this->getCheques());
                }
                $query = ChequeQuery::create(null, $criteria);
                if ($distinct) {
                    $query->distinct();
                }

                return $query
                    ->filterByDocumentoCheque($this)
                    ->count($con);
            }
        } else {
            return count($this->collCheques);
        }
    }

    /**
     * Method called to associate a Cheque object to this object
     * through the Cheque foreign key attribute.
     *
     * @param    Cheque $l Cheque
     * @return DocumentoCheque The current object (for fluent API support)
     */
    public function addCheque(Cheque $l)
    {
        if ($this->collCheques === null) {
            $this->initCheques();
            $this->collChequesPartial = true;
        }
        if (!$this->collCheques->contains($l)) { // only add it if the **same** object is not already associated
            $this->doAddCheque($l);
        }

        return $this;
    }

    /**
     * @param	Cheque $cheque The cheque object to add.
     */
    protected function doAddCheque($cheque)
    {
        $this->collCheques[]= $cheque;
        $cheque->setDocumentoCheque($this);
    }

    /**
     * @param	Cheque $cheque The cheque object to remove.
     */
    public function removeCheque($cheque)
    {
        if ($this->getCheques()->contains($cheque)) {
            $this->collCheques->remove($this->collCheques->search($cheque));
            if (null === $this->chequesScheduledForDeletion) {
                $this->chequesScheduledForDeletion = clone $this->collCheques;
                $this->chequesScheduledForDeletion->clear();
            }
            $this->chequesScheduledForDeletion[]= $cheque;
            $cheque->setDocumentoCheque(null);
        }
    }


