                    if (false !== $this->collGastoCajasPartial && count($collGastoCajas)) {
                      $this->initGastoCajas(false);

                      foreach($collGastoCajas as $obj) {
                        if (false == $this->collGastoCajas->contains($obj)) {
                          $this->collGastoCajas->append($obj);
                        }
                      }

                      $this->collGastoCajasPartial = true;
                    }

                    return $collGastoCajas;
                }

                if($partial && $this->collGastoCajas) {
                    foreach($this->collGastoCajas as $obj) {
                        if($obj->isNew()) {
                            $collGastoCajas[] = $obj;
                        }
                    }
                }

                $this->collGastoCajas = $collGastoCajas;
                $this->collGastoCajasPartial = false;
            }
        }

        return $this->collGastoCajas;
