     * and new objects from the given Propel collection.
     *
     * @param PropelCollection $gastoCajas A Propel collection.
     * @param PropelPDO $con Optional connection object
     */
    public function setGastoCajas(PropelCollection $gastoCajas, PropelPDO $con = null)
    {
        $this->gastoCajasScheduledForDeletion = $this->getGastoCajas(new Criteria(), $con)->diff($gastoCajas);

        foreach ($this->gastoCajasScheduledForDeletion as $gastoCajaRemoved) {
            $gastoCajaRemoved->setGasto(null);
        }

        $this->collGastoCajas = null;
        foreach ($gastoCajas as $gastoCaja) {
            $this->addGastoCaja($gastoCaja);
        }

        $this->collGastoCajas = $gastoCajas;
        $this->collGastoCajasPartial = false;
    }

    /**
     * Returns the number of related GastoCaja objects.
     *
     * @param Criteria $criteria
     * @param boolean $distinct
     * @param PropelPDO $con
     * @return int             Count of related GastoCaja objects.
     * @throws PropelException
     */
    public function countGastoCajas(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
    {
        $partial = $this->collGastoCajasPartial && !$this->isNew();
        if (null === $this->collGastoCajas || null !== $criteria || $partial) {
            if ($this->isNew() && null === $this->collGastoCajas) {
                return 0;
            } else {
                if($partial && !$criteria) {
                    return count($this->getGastoCajas());
                }
                $query = GastoCajaQuery::create(null, $criteria);
                if ($distinct) {
                    $query->distinct();
                }

                return $query
                    ->filterByGasto($this)
                    ->count($con);
            }
        } else {
            return count($this->collGastoCajas);
        }
    }

    /**
     * Method called to associate a GastoCaja object to this object
     * through the GastoCaja foreign key attribute.
