    public function addGastoCaja(GastoCaja $l)
    {
        if ($this->collGastoCajas === null) {
            $this->initGastoCajas();
            $this->collGastoCajasPartial = true;
        }
        if (!$this->collGastoCajas->contains($l)) { // only add it if the **same** object is not already associated
            $this->doAddGastoCaja($l);
        }

        return $this;
    }

    /**
