     */
    protected function doAddGastoCaja($gastoCaja)
    {
        $this->collGastoCajas[]= $gastoCaja;
        $gastoCaja->setGasto($this);
    }

    /**
     * @param	GastoCaja $gastoCaja The gastoCaja object to remove.
     */
    public function removeGastoCaja($gastoCaja)
    {
        if ($this->getGastoCajas()->contains($gastoCaja)) {
            $this->collGastoCajas->remove($this->collGastoCajas->search($gastoCaja));
            if (null === $this->gastoCajasScheduledForDeletion) {
                $this->gastoCajasScheduledForDeletion = clone $this->collGastoCajas;
                $this->gastoCajasScheduledForDeletion->clear();
            }
            $this->gastoCajasScheduledForDeletion[]= $gastoCaja;
            $gastoCaja->setGasto(null);
        }
    }


