    /**
     * Resets all references to other model objects or collections of model objects.
     *
     * This method is a user-space workaround for PHP's inability to garbage collect
     * objects with circular references (even in PHP 5.3). This is currently necessary
     * when using Propel in certain daemon or large-volumne/high-memory operations.
     *
     * @param boolean $deep Whether to also clear the references on all referrer objects.
     */
    public function clearAllReferences($deep = false)
    {
        if ($deep) {
            if ($this->collCuentaProveedors) {
                foreach ($this->collCuentaProveedors as $o) {
                    $o->clearAllReferences($deep);
                }
            }
            if ($this->collGastoDetalles) {
                foreach ($this->collGastoDetalles as $o) {
                    $o->clearAllReferences($deep);
                }
            }
            if ($this->collGastoPagos) {
                foreach ($this->collGastoPagos as $o) {
                    $o->clearAllReferences($deep);
                }
            }
            if ($this->collGastoCajas) {
                foreach ($this->collGastoCajas as $o) {
                    $o->clearAllReferences($deep);
                }
            }
        } // if ($deep)

        if ($this->collCuentaProveedors instanceof PropelCollection) {
            $this->collCuentaProveedors->clearIterator();
        }
        $this->collCuentaProveedors = null;
        if ($this->collGastoDetalles instanceof PropelCollection) {
            $this->collGastoDetalles->clearIterator();
        }
        $this->collGastoDetalles = null;
        if ($this->collGastoPagos instanceof PropelCollection) {
            $this->collGastoPagos->clearIterator();
        }
        $this->collGastoPagos = null;
        if ($this->collGastoCajas instanceof PropelCollection) {
            $this->collGastoCajas->clearIterator();
        }
        $this->collGastoCajas = null;
        $this->aEmpresa = null;
        $this->aProyecto = null;
        $this->aProveedor = null;
        $this->aOrdenProveedor = null;
        $this->aTienda = null;
    }

