     *
     * @param      int $pk the primary key.
     * @param      PropelPDO $con the connection to use
     * @return LibroAgrupado
     */
    public static function retrieveByPK($pk, PropelPDO $con = null)
    {

        if (null !== ($obj = LibroAgrupadoPeer::getInstanceFromPool((string) $pk))) {
            return $obj;
        }

        if ($con === null) {
            $con = Propel::getConnection(LibroAgrupadoPeer::DATABASE_NAME, Propel::CONNECTION_READ);
        }

        $criteria = new Criteria(LibroAgrupadoPeer::DATABASE_NAME);
        $criteria->add(LibroAgrupadoPeer::ID, $pk);

        $v = LibroAgrupadoPeer::doSelect($criteria, $con);

        return !empty($v) > 0 ? $v[0] : null;
    }

