     * @param      array $pks List of primary keys
     * @param      PropelPDO $con the connection to use
     * @return LibroAgrupado[]
     * @throws PropelException Any exceptions caught during processing will be
     *		 rethrown wrapped into a PropelException.
     */
    public static function retrieveByPKs($pks, PropelPDO $con = null)
    {
        if ($con === null) {
            $con = Propel::getConnection(LibroAgrupadoPeer::DATABASE_NAME, Propel::CONNECTION_READ);
        }

        $objs = null;
        if (empty($pks)) {
            $objs = array();
        } else {
            $criteria = new Criteria(LibroAgrupadoPeer::DATABASE_NAME);
            $criteria->add(LibroAgrupadoPeer::ID, $pks, Criteria::IN);
            $objs = LibroAgrupadoPeer::doSelect($criteria, $con);
        }

        return $objs;
    }

