    public static function retrieveByPK($pk, PropelPDO $con = null)
    {

        if (null !== ($obj = ListaActivosPeer::getInstanceFromPool((string) $pk))) {
            return $obj;
        }

        if ($con === null) {
            $con = Propel::getConnection(ListaActivosPeer::DATABASE_NAME, Propel::CONNECTION_READ);
        }

        $criteria = new Criteria(ListaActivosPeer::DATABASE_NAME);
        $criteria->add(ListaActivosPeer::ID, $pk);

