     * and new objects from the given Propel collection.
     *
     * @param PropelCollection $operacionPagos A Propel collection.
     * @param PropelPDO $con Optional connection object
     */
    public function setOperacionPagos(PropelCollection $operacionPagos, PropelPDO $con = null)
    {
        $this->operacionPagosScheduledForDeletion = $this->getOperacionPagos(new Criteria(), $con)->diff($operacionPagos);

        foreach ($this->operacionPagosScheduledForDeletion as $operacionPagoRemoved) {
            $operacionPagoRemoved->setOperacion(null);
        }

        $this->collOperacionPagos = null;
        foreach ($operacionPagos as $operacionPago) {
            $this->addOperacionPago($operacionPago);
        }

        $this->collOperacionPagos = $operacionPagos;
        $this->collOperacionPagosPartial = false;
    }

    /**
     * Returns the number of related OperacionPago objects.
     *
     * @param Criteria $criteria
     * @param boolean $distinct
     * @param PropelPDO $con
     * @return int             Count of related OperacionPago objects.
     * @throws PropelException
     */
    public function countOperacionPagos(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
    {
        $partial = $this->collOperacionPagosPartial && !$this->isNew();
        if (null === $this->collOperacionPagos || null !== $criteria || $partial) {
            if ($this->isNew() && null === $this->collOperacionPagos) {
                return 0;
            } else {
                if($partial && !$criteria) {
                    return count($this->getOperacionPagos());
                }
                $query = OperacionPagoQuery::create(null, $criteria);
                if ($distinct) {
                    $query->distinct();
                }

                return $query
                    ->filterByOperacion($this)
                    ->count($con);
            }
        } else {
            return count($this->collOperacionPagos);
        }
    }

    /**
