    public function addOperacionPago(OperacionPago $l)
    {
        if ($this->collOperacionPagos === null) {
            $this->initOperacionPagos();
            $this->collOperacionPagosPartial = true;
        }
        if (!$this->collOperacionPagos->contains($l)) { // only add it if the **same** object is not already associated
            $this->doAddOperacionPago($l);
        }

        return $this;
    }

    /**
     * @param	OperacionPago $operacionPago The operacionPago object to add.
     */
    protected function doAddOperacionPago($operacionPago)
    {
        $this->collOperacionPagos[]= $operacionPago;
        $operacionPago->setOperacion($this);
    }

    /**
     * @param	OperacionPago $operacionPago The operacionPago object to remove.
     */
    public function removeOperacionPago($operacionPago)
    {
        if ($this->getOperacionPagos()->contains($operacionPago)) {
            $this->collOperacionPagos->remove($this->collOperacionPagos->search($operacionPago));
            if (null === $this->operacionPagosScheduledForDeletion) {
                $this->operacionPagosScheduledForDeletion = clone $this->collOperacionPagos;
                $this->operacionPagosScheduledForDeletion->clear();
            }
            $this->operacionPagosScheduledForDeletion[]= $operacionPago;
            $operacionPago->setOperacion(null);
        }
    }


