     * when using Propel in certain daemon or large-volumne/high-memory operations.
     *
     * @param boolean $deep Whether to also clear the references on all referrer objects.
     */
    public function clearAllReferences($deep = false)
    {
        if ($deep) {
            if ($this->collOperacionDetalles) {
                foreach ($this->collOperacionDetalles as $o) {
                    $o->clearAllReferences($deep);
                }
            }
            if ($this->collOperacionPagos) {
                foreach ($this->collOperacionPagos as $o) {
                    $o->clearAllReferences($deep);
                }
            }
        } // if ($deep)

        if ($this->collOperacionDetalles instanceof PropelCollection) {
            $this->collOperacionDetalles->clearIterator();
        }
        $this->collOperacionDetalles = null;
        if ($this->collOperacionPagos instanceof PropelCollection) {
            $this->collOperacionPagos->clearIterator();
        }
        $this->collOperacionPagos = null;
        $this->aEmpresa = null;
        $this->aTienda = null;
        $this->aRecetario = null;
        $this->aCliente = null;
        $this->aVendedor = null;
        $this->aPais = null;
    }

