            $criteria->setDbName(OperacionPeer::DATABASE_NAME);
        }

        OperacionPeer::addSelectColumns($criteria);
        $startcol = OperacionPeer::NUM_HYDRATE_COLUMNS;
        EmpresaPeer::addSelectColumns($criteria);

        $criteria->addJoin(OperacionPeer::EMPRESA_ID, EmpresaPeer::ID, $join_behavior);

        // symfony_behaviors behavior
        foreach (sfMixer::getCallables(self::getMixerPreSelectHook(__FUNCTION__)) as $sf_hook)
        {
          call_user_func($sf_hook, 'BaseOperacionPeer', $criteria, $con);
        }

        $stmt = BasePeer::doSelect($criteria, $con);
        $results = array();

        while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
            $key1 = OperacionPeer::getPrimaryKeyHashFromRow($row, 0);
            if (null !== ($obj1 = OperacionPeer::getInstanceFromPool($key1))) {
                // We no longer rehydrate the object, since this can cause data loss.
