        $startcol5 = $startcol4 + RecetarioPeer::NUM_HYDRATE_COLUMNS;

        ClientePeer::addSelectColumns($criteria);
        $startcol6 = $startcol5 + ClientePeer::NUM_HYDRATE_COLUMNS;

        VendedorPeer::addSelectColumns($criteria);
        $startcol7 = $startcol6 + VendedorPeer::NUM_HYDRATE_COLUMNS;

        $criteria->addJoin(OperacionPeer::EMPRESA_ID, EmpresaPeer::ID, $join_behavior);

        $criteria->addJoin(OperacionPeer::TIENDA_ID, TiendaPeer::ID, $join_behavior);

        $criteria->addJoin(OperacionPeer::RECETARIO_ID, RecetarioPeer::ID, $join_behavior);

        $criteria->addJoin(OperacionPeer::CLIENTE_ID, ClientePeer::ID, $join_behavior);

        $criteria->addJoin(OperacionPeer::VENDEDOR_ID, VendedorPeer::ID, $join_behavior);

        // symfony_behaviors behavior
        foreach (sfMixer::getCallables(self::getMixerPreSelectHook(__FUNCTION__)) as $sf_hook)
        {
          call_user_func($sf_hook, 'BaseOperacionPeer', $criteria, $con);
        }


        $stmt = BasePeer::doSelect($criteria, $con);
        $results = array();

        while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
            $key1 = OperacionPeer::getPrimaryKeyHashFromRow($row, 0);
            if (null !== ($obj1 = OperacionPeer::getInstanceFromPool($key1))) {
                // We no longer rehydrate the object, since this can cause data loss.
                // See http://www.propelorm.org/ticket/509
                // $obj1->hydrate($row, 0, true); // rehydrate
            } else {
                $cls = OperacionPeer::getOMClass();

                $obj1 = new $cls();
                $obj1->hydrate($row);
                OperacionPeer::addInstanceToPool($obj1, $key1);
            } // if obj1 already loaded

                // Add objects for joined Empresa rows

                $key2 = EmpresaPeer::getPrimaryKeyHashFromRow($row, $startcol2);
                if ($key2 !== null) {
                    $obj2 = EmpresaPeer::getInstanceFromPool($key2);
                    if (!$obj2) {

                        $cls = EmpresaPeer::getOMClass();

                    $obj2 = new $cls();
                    $obj2->hydrate($row, $startcol2);
                    EmpresaPeer::addInstanceToPool($obj2, $key2);
                } // if $obj2 already loaded

                // Add the $obj1 (Operacion) to the collection in $obj2 (Empresa)
                $obj2->addOperacion($obj1);

            } // if joined row is not null

                // Add objects for joined Tienda rows

                $key3 = TiendaPeer::getPrimaryKeyHashFromRow($row, $startcol3);
                if ($key3 !== null) {
                    $obj3 = TiendaPeer::getInstanceFromPool($key3);
                    if (!$obj3) {

                        $cls = TiendaPeer::getOMClass();

                    $obj3 = new $cls();
                    $obj3->hydrate($row, $startcol3);
                    TiendaPeer::addInstanceToPool($obj3, $key3);
                } // if $obj3 already loaded

                // Add the $obj1 (Operacion) to the collection in $obj3 (Tienda)
                $obj3->addOperacion($obj1);

            } // if joined row is not null

                // Add objects for joined Recetario rows

                $key4 = RecetarioPeer::getPrimaryKeyHashFromRow($row, $startcol4);
                if ($key4 !== null) {
                    $obj4 = RecetarioPeer::getInstanceFromPool($key4);
                    if (!$obj4) {

                        $cls = RecetarioPeer::getOMClass();

                    $obj4 = new $cls();
                    $obj4->hydrate($row, $startcol4);
                    RecetarioPeer::addInstanceToPool($obj4, $key4);
                } // if $obj4 already loaded

                // Add the $obj1 (Operacion) to the collection in $obj4 (Recetario)
                $obj4->addOperacion($obj1);

            } // if joined row is not null

                // Add objects for joined Cliente rows

                $key5 = ClientePeer::getPrimaryKeyHashFromRow($row, $startcol5);
                if ($key5 !== null) {
                    $obj5 = ClientePeer::getInstanceFromPool($key5);
                    if (!$obj5) {

                        $cls = ClientePeer::getOMClass();

                    $obj5 = new $cls();
                    $obj5->hydrate($row, $startcol5);
                    ClientePeer::addInstanceToPool($obj5, $key5);
                } // if $obj5 already loaded

                // Add the $obj1 (Operacion) to the collection in $obj5 (Cliente)
                $obj5->addOperacion($obj1);

            } // if joined row is not null

                // Add objects for joined Vendedor rows

                $key6 = VendedorPeer::getPrimaryKeyHashFromRow($row, $startcol6);
                if ($key6 !== null) {
                    $obj6 = VendedorPeer::getInstanceFromPool($key6);
                    if (!$obj6) {

                        $cls = VendedorPeer::getOMClass();

                    $obj6 = new $cls();
                    $obj6->hydrate($row, $startcol6);
                    VendedorPeer::addInstanceToPool($obj6, $key6);
                } // if $obj6 already loaded

                // Add the $obj1 (Operacion) to the collection in $obj6 (Vendedor)
                $obj6->addOperacion($obj1);

            } // if joined row is not null

            $results[] = $obj1;
        }
        $stmt->closeCursor();

        return $results;
    }

