     */
    public function addGasto(Gasto $l)
    {
        if ($this->collGastos === null) {
            $this->initGastos();
            $this->collGastosPartial = true;
        }
        if (!$this->collGastos->contains($l)) { // only add it if the **same** object is not already associated
            $this->doAddGasto($l);
        }

        return $this;
    }

    /**
