    public static function doUpdate($values, PropelPDO $con = null)
    {
        if ($con === null) {
            $con = Propel::getConnection(OrdenVendedorPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
        }

        $selectCriteria = new Criteria(OrdenVendedorPeer::DATABASE_NAME);

        if ($values instanceof Criteria) {
            $criteria = clone $values; // rename for clarity

            $comparison = $criteria->getComparison(OrdenVendedorPeer::ID);
            $value = $criteria->remove(OrdenVendedorPeer::ID);
            if ($value) {
                $selectCriteria->add(OrdenVendedorPeer::ID, $value, $comparison);
            } else {
                $selectCriteria->setPrimaryTableName(OrdenVendedorPeer::TABLE_NAME);
            }

        } else { // $values is OrdenVendedor object
            $criteria = $values->buildCriteria(); // gets full criteria
            $selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
        }

        // set the correct dbName
        $criteria->setDbName(OrdenVendedorPeer::DATABASE_NAME);

        return BasePeer::doUpdate($selectCriteria, $criteria, $con);
