        if (!$this->collPrestamoDetalles->contains($l)) { // only add it if the **same** object is not already associated
            $this->doAddPrestamoDetalle($l);
        }

        return $this;
    }

    /**
     * @param	PrestamoDetalle $prestamoDetalle The prestamoDetalle object to add.
     */
    protected function doAddPrestamoDetalle($prestamoDetalle)
    {
        $this->collPrestamoDetalles[]= $prestamoDetalle;
        $prestamoDetalle->setPrestamo($this);
    }

    /**
     * @param	PrestamoDetalle $prestamoDetalle The prestamoDetalle object to remove.
     */
    public function removePrestamoDetalle($prestamoDetalle)
    {
        if ($this->getPrestamoDetalles()->contains($prestamoDetalle)) {
            $this->collPrestamoDetalles->remove($this->collPrestamoDetalles->search($prestamoDetalle));
            if (null === $this->prestamoDetallesScheduledForDeletion) {
                $this->prestamoDetallesScheduledForDeletion = clone $this->collPrestamoDetalles;
                $this->prestamoDetallesScheduledForDeletion->clear();
            }
            $this->prestamoDetallesScheduledForDeletion[]= $prestamoDetalle;
            $prestamoDetalle->setPrestamo(null);
        }
    }


