    public static function doInsert($values, PropelPDO $con = null)
    {
        if ($con === null) {
            $con = Propel::getConnection(PrestamoDetallePeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
        }

        if ($values instanceof Criteria) {
            $criteria = clone $values; // rename for clarity
        } else {
            $criteria = $values->buildCriteria(); // build Criteria from PrestamoDetalle object
        }

        if ($criteria->containsKey(PrestamoDetallePeer::ID) && $criteria->keyContainsValue(PrestamoDetallePeer::ID) ) {
            throw new PropelException('Cannot insert a value for auto-increment primary key ('.PrestamoDetallePeer::ID.')');
        }


        // Set the correct dbName
        $criteria->setDbName(PrestamoDetallePeer::DATABASE_NAME);

        try {
            // use transaction because $criteria could contain info
            // for more than one table (I guess, conceivably)
            $con->beginTransaction();
            $pk = BasePeer::doInsert($criteria, $con);
            $con->commit();
        } catch (PropelException $e) {
            $con->rollBack();
            throw $e;
        }

        return $pk;
