    /**
     * Filter the query by a related PrestamoDetalle object
     *
     * @param   PrestamoDetalle|PropelObjectCollection $prestamoDetalle  the related object to use as filter
     * @param     string $comparison Operator to use for the column comparison, defaults to Criteria::EQUAL
     *
     * @return   PrestamoQuery The current query, for fluid interface
     * @throws   PropelException - if the provided filter is invalid.
     */
    public function filterByPrestamoDetalle($prestamoDetalle, $comparison = null)
    {
        if ($prestamoDetalle instanceof PrestamoDetalle) {
            return $this
                ->addUsingAlias(PrestamoPeer::ID, $prestamoDetalle->getPrestamoId(), $comparison);
        } elseif ($prestamoDetalle instanceof PropelObjectCollection) {
            return $this
                ->usePrestamoDetalleQuery()
                ->filterByPrimaryKeys($prestamoDetalle->getPrimaryKeys())
                ->endUse();
        } else {
            throw new PropelException('filterByPrestamoDetalle() only accepts arguments of type PrestamoDetalle or PropelCollection');
        }
    }

    /**
     * Adds a JOIN clause to the query using the PrestamoDetalle relation
     *
     * @param     string $relationAlias optional alias for the relation
     * @param     string $joinType Accepted values are null, 'left join', 'right join', 'inner join'
     *
     * @return PrestamoQuery The current query, for fluid interface
     */
    public function joinPrestamoDetalle($relationAlias = null, $joinType = Criteria::LEFT_JOIN)
    {
        $tableMap = $this->getTableMap();
        $relationMap = $tableMap->getRelation('PrestamoDetalle');

        // create a ModelJoin object for this join
        $join = new ModelJoin();
        $join->setJoinType($joinType);
        $join->setRelationMap($relationMap, $this->useAliasInSQL ? $this->getModelAlias() : null, $relationAlias);
        if ($previousJoin = $this->getPreviousJoin()) {
            $join->setPreviousJoin($previousJoin);
        }

        // add the ModelJoin to the current object
        if ($relationAlias) {
            $this->addAlias($relationAlias, $relationMap->getRightTable()->getName());
            $this->addJoinObject($join, $relationAlias);
        } else {
            $this->addJoinObject($join, 'PrestamoDetalle');
        }

        return $this;
    }

