            ->useQuery($relationAlias ? $relationAlias : 'ListaPrecio', 'ListaPrecioQuery');
    }

    /**
     * Exclude object from result
     *
     * @param   ProductoPrecio $productoPrecio Object to remove from the list of results
     *
     * @return ProductoPrecioQuery The current query, for fluid interface
     */
    public function prune($productoPrecio = null)
    {
        if ($productoPrecio) {
            $this->addUsingAlias(ProductoPrecioPeer::ID, $productoPrecio->getId(), Criteria::NOT_EQUAL);
        }

        return $this;
    }

}
