     * Use the Empresa relation Empresa object
     *
     * @see       useQuery()
     *
     * @param     string $relationAlias optional alias for the relation,
     *                                   to be used as main alias in the secondary query
     * @param     string $joinType Accepted values are null, 'left join', 'right join', 'inner join'
     *
     * @return   EmpresaQuery A secondary query class using the current class as primary query
     */
    public function useEmpresaQuery($relationAlias = null, $joinType = Criteria::LEFT_JOIN)
    {
        return $this
            ->joinEmpresa($relationAlias, $joinType)
            ->useQuery($relationAlias ? $relationAlias : 'Empresa', 'EmpresaQuery');
    }

    /**
     * Filter the query by a related Gasto object
     *
     * @param   Gasto|PropelObjectCollection $gasto  the related object to use as filter
     * @param     string $comparison Operator to use for the column comparison, defaults to Criteria::EQUAL
     *
     * @return   ProyectoQuery The current query, for fluid interface
     * @throws   PropelException - if the provided filter is invalid.
     */
    public function filterByGasto($gasto, $comparison = null)
    {
        if ($gasto instanceof Gasto) {
            return $this
                ->addUsingAlias(ProyectoPeer::ID, $gasto->getProyectoId(), $comparison);
        } elseif ($gasto instanceof PropelObjectCollection) {
            return $this
                ->useGastoQuery()
                ->filterByPrimaryKeys($gasto->getPrimaryKeys())
                ->endUse();
        } else {
            throw new PropelException('filterByGasto() only accepts arguments of type Gasto or PropelCollection');
        }
    }

    /**
     * Adds a JOIN clause to the query using the Gasto relation
     *
     * @param     string $relationAlias optional alias for the relation
     * @param     string $joinType Accepted values are null, 'left join', 'right join', 'inner join'
     *
     * @return ProyectoQuery The current query, for fluid interface
     */
    public function joinGasto($relationAlias = null, $joinType = Criteria::LEFT_JOIN)
    {
        $tableMap = $this->getTableMap();
        $relationMap = $tableMap->getRelation('Gasto');

        // create a ModelJoin object for this join
        $join = new ModelJoin();
        $join->setJoinType($joinType);
        $join->setRelationMap($relationMap, $this->useAliasInSQL ? $this->getModelAlias() : null, $relationAlias);
        if ($previousJoin = $this->getPreviousJoin()) {
            $join->setPreviousJoin($previousJoin);
        }

        // add the ModelJoin to the current object
        if ($relationAlias) {
            $this->addAlias($relationAlias, $relationMap->getRightTable()->getName());
            $this->addJoinObject($join, $relationAlias);
        } else {
            $this->addJoinObject($join, 'Gasto');
        }

        return $this;
    }

    /**
     * Use the Gasto relation Gasto object
     *
     * @see       useQuery()
     *
     * @param     string $relationAlias optional alias for the relation,
     *                                   to be used as main alias in the secondary query
     * @param     string $joinType Accepted values are null, 'left join', 'right join', 'inner join'
     *
     * @return   GastoQuery A secondary query class using the current class as primary query
     */
    public function useGastoQuery($relationAlias = null, $joinType = Criteria::LEFT_JOIN)
    {
        return $this
            ->joinGasto($relationAlias, $joinType)
            ->useQuery($relationAlias ? $relationAlias : 'Gasto', 'GastoQuery');
    }

    /**
     * Exclude object from result
     *
     * @param   Proyecto $proyecto Object to remove from the list of results
     *
     * @return ProyectoQuery The current query, for fluid interface
     */
    public function prune($proyecto = null)
    {
        if ($proyecto) {
            $this->addUsingAlias(ProyectoPeer::ID, $proyecto->getId(), Criteria::NOT_EQUAL);
        }

        return $this;
    }

}
