     *		 rethrown wrapped into a PropelException.
     */
    public static function doInsert($values, PropelPDO $con = null)
    {
        if ($con === null) {
            $con = Propel::getConnection(RecetaProductoPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
        }

        if ($values instanceof Criteria) {
            $criteria = clone $values; // rename for clarity
        } else {
            $criteria = $values->buildCriteria(); // build Criteria from RecetaProducto object
        }

        if ($criteria->containsKey(RecetaProductoPeer::ID) && $criteria->keyContainsValue(RecetaProductoPeer::ID) ) {
            throw new PropelException('Cannot insert a value for auto-increment primary key ('.RecetaProductoPeer::ID.')');
        }


        // Set the correct dbName
        $criteria->setDbName(RecetaProductoPeer::DATABASE_NAME);

        try {
            // use transaction because $criteria could contain info
            // for more than one table (I guess, conceivably)
            $con->beginTransaction();
            $pk = BasePeer::doInsert($criteria, $con);
            $con->commit();
        } catch (PropelException $e) {
            $con->rollBack();
            throw $e;
        }

        return $pk;
