        }

        $selectCriteria = new Criteria(RecetaProductoPeer::DATABASE_NAME);

        if ($values instanceof Criteria) {
            $criteria = clone $values; // rename for clarity

            $comparison = $criteria->getComparison(RecetaProductoPeer::ID);
            $value = $criteria->remove(RecetaProductoPeer::ID);
            if ($value) {
                $selectCriteria->add(RecetaProductoPeer::ID, $value, $comparison);
            } else {
                $selectCriteria->setPrimaryTableName(RecetaProductoPeer::TABLE_NAME);
            }

        } else { // $values is RecetaProducto object
            $criteria = $values->buildCriteria(); // gets full criteria
            $selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
        }

        // set the correct dbName
        $criteria->setDbName(RecetaProductoPeer::DATABASE_NAME);

        return BasePeer::doUpdate($selectCriteria, $criteria, $con);
