    /**
     * Resets all references to other model objects or collections of model objects.
     *
     * This method is a user-space workaround for PHP's inability to garbage collect
     * objects with circular references (even in PHP 5.3). This is currently necessary
     * when using Propel in certain daemon or large-volumne/high-memory operations.
     *
     * @param boolean $deep Whether to also clear the references on all referrer objects.
     */
    public function clearAllReferences($deep = false)
    {
        if ($deep) {
            if ($this->collOperacions) {
                foreach ($this->collOperacions as $o) {
                    $o->clearAllReferences($deep);
                }
            }
            if ($this->collOrdenCotizacions) {
                foreach ($this->collOrdenCotizacions as $o) {
                    $o->clearAllReferences($deep);
                }
            }
            if ($this->collRecetarioDetalles) {
                foreach ($this->collRecetarioDetalles as $o) {
                    $o->clearAllReferences($deep);
                }
            }
        } // if ($deep)

        if ($this->collOperacions instanceof PropelCollection) {
            $this->collOperacions->clearIterator();
        }
        $this->collOperacions = null;
        if ($this->collOrdenCotizacions instanceof PropelCollection) {
            $this->collOrdenCotizacions->clearIterator();
        }
        $this->collOrdenCotizacions = null;
        if ($this->collRecetarioDetalles instanceof PropelCollection) {
            $this->collRecetarioDetalles->clearIterator();
        }
        $this->collRecetarioDetalles = null;
        $this->aCliente = null;
        $this->aEmpresa = null;
    }

