     * @param     string $joinType Accepted values are null, 'left join', 'right join', 'inner join'
     *
     * @return   ProductoQuery A secondary query class using the current class as primary query
     */
    public function useProductoQuery($relationAlias = null, $joinType = Criteria::LEFT_JOIN)
    {
        return $this
            ->joinProducto($relationAlias, $joinType)
            ->useQuery($relationAlias ? $relationAlias : 'Producto', 'ProductoQuery');
    }

    /**
     * Exclude object from result
     *
     * @param   RecetarioDetalle $recetarioDetalle Object to remove from the list of results
     *
     * @return RecetarioDetalleQuery The current query, for fluid interface
     */
    public function prune($recetarioDetalle = null)
    {
        if ($recetarioDetalle) {
            $this->addUsingAlias(RecetarioDetallePeer::ID, $recetarioDetalle->getId(), Criteria::NOT_EQUAL);
        }

        return $this;
    }

}
