     * @param boolean $makeNew Whether to reset autoincrement PKs and make the object new.
     * @throws PropelException
     */
    public function copyInto($copyObj, $deepCopy = false, $makeNew = true)
    {
        $copyObj->setFecha($this->getFecha());
        $copyObj->setSaldoLibro($this->getSaldoLibro());
        $copyObj->setBancoId($this->getBancoId());
        $copyObj->setUsuario($this->getUsuario());
        $copyObj->setCreatedAt($this->getCreatedAt());
        $copyObj->setUpdatedAt($this->getUpdatedAt());
        $copyObj->setSaldoBanco($this->getSaldoBanco());
        $copyObj->setSaldoConciliado($this->getSaldoConciliado());
        $copyObj->setDiferencia($this->getDiferencia());
        $copyObj->setFechaDocu($this->getFechaDocu());
        $copyObj->setDepositoTransito($this->getDepositoTransito());
        $copyObj->setNotaCredito($this->getNotaCredito());
        $copyObj->setChequesCircula($this->getChequesCircula());
        $copyObj->setNotaTransito($this->getNotaTransito());
        $copyObj->setDiferencial($this->getDiferencial());

        if ($deepCopy && !$this->startCopy) {
            // important: temporarily setNew(false) because this affects the behavior of
            // the getter/setter methods for fkey referrer objects.
            $copyObj->setNew(false);
            // store object hash to prevent cycle
            $this->startCopy = true;

            //unflag object copy
            $this->startCopy = false;
        } // if ($deepCopy)

        if ($makeNew) {
            $copyObj->setNew(true);
            $copyObj->setId(NULL); // this is a auto-increment column, so set to default value
        }
    }

