            if ($this->operacionDetallesScheduledForDeletion !== null) {
                if (!$this->operacionDetallesScheduledForDeletion->isEmpty()) {
                    foreach ($this->operacionDetallesScheduledForDeletion as $operacionDetalle) {
                        // need to save related object because we set the relation to null
                        $operacionDetalle->save($con);
                    }
                    $this->operacionDetallesScheduledForDeletion = null;
                }
            }

            if ($this->collOperacionDetalles !== null) {
                foreach ($this->collOperacionDetalles as $referrerFK) {
                    if (!$referrerFK->isDeleted()) {
                        $affectedRows += $referrerFK->save($con);
                    }
                }
            }

            if ($this->ordenProveedorDetallesScheduledForDeletion !== null) {
                if (!$this->ordenProveedorDetallesScheduledForDeletion->isEmpty()) {
                    foreach ($this->ordenProveedorDetallesScheduledForDeletion as $ordenProveedorDetalle) {
