                    $this->ordenProveedorDetallesScheduledForDeletion = null;
                }
            }

            if ($this->collOrdenProveedorDetalles !== null) {
                foreach ($this->collOrdenProveedorDetalles as $referrerFK) {
                    if (!$referrerFK->isDeleted()) {
                        $affectedRows += $referrerFK->save($con);
                    }
                }
            }

            if ($this->ordenCotizacionDetallesScheduledForDeletion !== null) {
                if (!$this->ordenCotizacionDetallesScheduledForDeletion->isEmpty()) {
                    foreach ($this->ordenCotizacionDetallesScheduledForDeletion as $ordenCotizacionDetalle) {
