        }
        if ($this->isColumnModified(ServicioPeer::CUENTA_CONTABLE)) {
            $modifiedColumns[':p' . $index++]  = '`CUENTA_CONTABLE`';
        }
        if ($this->isColumnModified(ServicioPeer::IMPUESTO_HOTELERO)) {
            $modifiedColumns[':p' . $index++]  = '`IMPUESTO_HOTELERO`';
        }

        $sql = sprintf(
            'INSERT INTO `servicio` (%s) VALUES (%s)',
            implode(', ', $modifiedColumns),
            implode(', ', array_keys($modifiedColumns))
        );

        try {
            $stmt = $con->prepare($sql);
            foreach ($modifiedColumns as $identifier => $columnName) {
                switch ($columnName) {
                    case '`ID`':
                        $stmt->bindValue($identifier, $this->id, PDO::PARAM_INT);
                        break;
                    case '`CODIGO`':
                        $stmt->bindValue($identifier, $this->codigo, PDO::PARAM_STR);
                        break;
                    case '`NOMBRE`':
                        $stmt->bindValue($identifier, $this->nombre, PDO::PARAM_STR);
                        break;
                    case '`ACTIVO`':
                        $stmt->bindValue($identifier, (int) $this->activo, PDO::PARAM_INT);
                        break;
                    case '`CREATED_BY`':
                        $stmt->bindValue($identifier, $this->created_by, PDO::PARAM_STR);
                        break;
                    case '`UPDATED_BY`':
                        $stmt->bindValue($identifier, $this->updated_by, PDO::PARAM_STR);
                        break;
                    case '`CREATED_AT`':
                        $stmt->bindValue($identifier, $this->created_at, PDO::PARAM_STR);
                        break;
                    case '`UPDATED_AT`':
                        $stmt->bindValue($identifier, $this->updated_at, PDO::PARAM_STR);
                        break;
                    case '`OBSERVACIONES`':
                        $stmt->bindValue($identifier, $this->observaciones, PDO::PARAM_STR);
                        break;
                    case '`EMPRESA_ID`':
                        $stmt->bindValue($identifier, $this->empresa_id, PDO::PARAM_INT);
                        break;
                    case '`PRECIO`':
                        $stmt->bindValue($identifier, $this->precio, PDO::PARAM_STR);
                        break;
                    case '`CUENTA_CONTABLE`':
                        $stmt->bindValue($identifier, $this->cuenta_contable, PDO::PARAM_STR);
                        break;
                    case '`IMPUESTO_HOTELERO`':
                        $stmt->bindValue($identifier, (int) $this->impuesto_hotelero, PDO::PARAM_INT);
                        break;
                }
            }
            $stmt->execute();
        } catch (Exception $e) {
            Propel::log($e->getMessage(), Propel::LOG_ERR);
            throw new PropelException(sprintf('Unable to execute INSERT statement [%s]', $sql), $e);
        }

        try {
            $pk = $con->lastInsertId();
        } catch (Exception $e) {
            throw new PropelException('Unable to get autoincrement id.', $e);
        }
        $this->setId($pk);

        $this->setNew(false);
    }

