     * </code>
     *
     * @param     string $cuentaContable The value to use as filter.
     *              Accepts wildcards (* and % trigger a LIKE)
     * @param     string $comparison Operator to use for the column comparison, defaults to Criteria::EQUAL
     *
     * @return ServicioQuery The current query, for fluid interface
     */
    public function filterByCuentaContable($cuentaContable = null, $comparison = null)
    {
        if (null === $comparison) {
            if (is_array($cuentaContable)) {
                $comparison = Criteria::IN;
            } elseif (preg_match('/[\%\*]/', $cuentaContable)) {
                $cuentaContable = str_replace('*', '%', $cuentaContable);
