     * @param      PropelPDO $con
     * @param      String    $join_behavior the type of joins to use, defaults to Criteria::LEFT_JOIN
     * @return array           Array of Tienda objects.
     * @throws PropelException Any exceptions caught during processing will be
     *		 rethrown wrapped into a PropelException.
     */
    public static function doSelectJoinAllExceptEmpresa(Criteria $criteria, $con = null, $join_behavior = Criteria::LEFT_JOIN)
    {
        $criteria = clone $criteria;

        // Set the correct dbName if it has not been overridden
        // $criteria->getDbName() will return the same object if not set to another value
        // so == check is okay and faster
        if ($criteria->getDbName() == Propel::getDefaultDB()) {
            $criteria->setDbName(TiendaPeer::DATABASE_NAME);
        }

        TiendaPeer::addSelectColumns($criteria);
        $startcol2 = TiendaPeer::NUM_HYDRATE_COLUMNS;

        DepartamentoPeer::addSelectColumns($criteria);
        $startcol3 = $startcol2 + DepartamentoPeer::NUM_HYDRATE_COLUMNS;

        MunicipioPeer::addSelectColumns($criteria);
        $startcol4 = $startcol3 + MunicipioPeer::NUM_HYDRATE_COLUMNS;

        $criteria->addJoin(TiendaPeer::DEPARTAMENTO_ID, DepartamentoPeer::ID, $join_behavior);

        $criteria->addJoin(TiendaPeer::MUNICIPIO_ID, MunicipioPeer::ID, $join_behavior);

        // symfony_behaviors behavior
        foreach (sfMixer::getCallables(self::getMixerPreSelectHook(__FUNCTION__)) as $sf_hook)
        {
          call_user_func($sf_hook, 'BaseTiendaPeer', $criteria, $con);
        }


        $stmt = BasePeer::doSelect($criteria, $con);
        $results = array();

        while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
            $key1 = TiendaPeer::getPrimaryKeyHashFromRow($row, 0);
            if (null !== ($obj1 = TiendaPeer::getInstanceFromPool($key1))) {
                // We no longer rehydrate the object, since this can cause data loss.
                // See http://www.propelorm.org/ticket/509
                // $obj1->hydrate($row, 0, true); // rehydrate
            } else {
                $cls = TiendaPeer::getOMClass();

                $obj1 = new $cls();
                $obj1->hydrate($row);
                TiendaPeer::addInstanceToPool($obj1, $key1);
            } // if obj1 already loaded

                // Add objects for joined Departamento rows

                $key2 = DepartamentoPeer::getPrimaryKeyHashFromRow($row, $startcol2);
                if ($key2 !== null) {
                    $obj2 = DepartamentoPeer::getInstanceFromPool($key2);
                    if (!$obj2) {

                        $cls = DepartamentoPeer::getOMClass();

                    $obj2 = new $cls();
                    $obj2->hydrate($row, $startcol2);
                    DepartamentoPeer::addInstanceToPool($obj2, $key2);
                } // if $obj2 already loaded

                // Add the $obj1 (Tienda) to the collection in $obj2 (Departamento)
                $obj2->addTienda($obj1);

            } // if joined row is not null

                // Add objects for joined Municipio rows

                $key3 = MunicipioPeer::getPrimaryKeyHashFromRow($row, $startcol3);
                if ($key3 !== null) {
                    $obj3 = MunicipioPeer::getInstanceFromPool($key3);
                    if (!$obj3) {

                        $cls = MunicipioPeer::getOMClass();

                    $obj3 = new $cls();
                    $obj3->hydrate($row, $startcol3);
                    MunicipioPeer::addInstanceToPool($obj3, $key3);
                } // if $obj3 already loaded

                // Add the $obj1 (Tienda) to the collection in $obj3 (Municipio)
                $obj3->addTienda($obj1);

            } // if joined row is not null

            $results[] = $obj1;
        }
        $stmt->closeCursor();

        return $results;
    }


