     *
     * @param   UsuarioReport $usuarioReport Object to remove from the list of results
     *
     * @return UsuarioReportQuery The current query, for fluid interface
     */
    public function prune($usuarioReport = null)
    {
        if ($usuarioReport) {
            $this->addUsingAlias(UsuarioReportPeer::ID, $usuarioReport->getId(), Criteria::NOT_EQUAL);
        }

        return $this;
    }

