    /**
     * Sets contents of passed object to values from current object.
     *
     * If desired, this method can also make copies of all associated (fkey referrers)
     * objects.
     *
     * @param object $copyObj An object of VentasVendedor (or compatible) type.
     * @param boolean $deepCopy Whether to also copy all rows that refer (by fkey) to the current row.
     * @param boolean $makeNew Whether to reset autoincrement PKs and make the object new.
     * @throws PropelException
     */
    public function copyInto($copyObj, $deepCopy = false, $makeNew = true)
    {
        $copyObj->setCodigoVendedor($this->getCodigoVendedor());
        $copyObj->setNombreVendedor($this->getNombreVendedor());
        $copyObj->setFacturas($this->getFacturas());
        $copyObj->setTotalFacturas($this->getTotalFacturas());
        $copyObj->setDetalleFacturas($this->getDetalleFacturas());
        $copyObj->setNotas($this->getNotas());
        $copyObj->setTotalNotas($this->getTotalNotas());
        $copyObj->setDetalleNotas($this->getDetalleNotas());
        $copyObj->setPeriodo($this->getPeriodo());
        $copyObj->setMes($this->getMes());
        $copyObj->setCreatedAt($this->getCreatedAt());
        $copyObj->setUpdatedAt($this->getUpdatedAt());
        $copyObj->setCreatedBy($this->getCreatedBy());
        $copyObj->setUpdatedBy($this->getUpdatedBy());
        $copyObj->setEmpresaId($this->getEmpresaId());
        $copyObj->setBono($this->getBono());

        if ($deepCopy && !$this->startCopy) {
            // important: temporarily setNew(false) because this affects the behavior of
            // the getter/setter methods for fkey referrer objects.
            $copyObj->setNew(false);
            // store object hash to prevent cycle
            $this->startCopy = true;

            //unflag object copy
            $this->startCopy = false;
        } // if ($deepCopy)

        if ($makeNew) {
            $copyObj->setNew(true);
            $copyObj->setId(NULL); // this is a auto-increment column, so set to default value
        }
    }

