    public function prune($ventasVendedor = null)
    {
        if ($ventasVendedor) {
            $this->addUsingAlias(VentasVendedorPeer::ID, $ventasVendedor->getId(), Criteria::NOT_EQUAL);
        }

        return $this;
    }

